// src/components/GroupBookingForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import API from '../api';  // booking-service client

// Returns array of YYYY-MM-DD strings from start through end, inclusive.
function getDatesBetween(start, end) {
  const dates = [];
  let curr = new Date(start);
  const last = new Date(end);
  while (curr <= last) {
    dates.push(curr.toISOString().split('T')[0]);
    curr.setDate(curr.getDate() + 1);
  }
  return dates;
}

const GroupBookingForm = () => {
  const navigate = useNavigate();

  const [hotels, setHotels] = useState([]);
  const [selectedHotel, setSelectedHotel] = useState('');
  const [rooms, setRooms] = useState([]);
  const [filteredRooms, setFilteredRooms] = useState([]);

  const [typeFilter, setTypeFilter] = useState({
    Single: false,
    Double: false,
    Suite: false
  });

  const [filterDates, setFilterDates] = useState({
    checkIn: '',
    checkOut: ''
  });

  const [selectedRoomIds, setSelectedRoomIds] = useState(new Set());
  const [formData, setFormData] = useState({
    agentEmail: '',
    discountRate: 0
  });

  const [message, setMessage] = useState('');

  // 1) Load hotels
  useEffect(() => {
    axios.get('http://localhost:5000/api/hotels')
      .then(r => setHotels(r.data))
      .catch(err => console.error('Error fetching hotels:', err));
  }, []);

  // 2) Load rooms when a hotel is selected
  useEffect(() => {
    if (!selectedHotel) return;
    axios.get('http://localhost:5000/api/rooms', { params: { hotel: selectedHotel } })
      .then(r => setRooms(r.data))
      .catch(err => console.error('Error fetching rooms:', err));

    // reset everything
    setTypeFilter({ Single:false, Double:false, Suite:false });
    setFilterDates({ checkIn:'', checkOut:'' });
    setSelectedRoomIds(new Set());
    setFilteredRooms([]);
  }, [selectedHotel]);

  // 3) Filter whenever rooms, typeFilter, or dates change
  useEffect(() => {
    // Only filter if both dates are present
    const { checkIn, checkOut } = filterDates;
    const dateRange = (checkIn && checkOut)
      ? getDatesBetween(checkIn, checkOut)
      : [];

    // Which types are checked?
    const types = Object.entries(typeFilter)
      .filter(([_, enabled]) => enabled)
      .map(([type]) => type);

    console.log('--- Filtering Rooms ---');
    console.log('Selected Types:', types);
    console.log('Date Range:', dateRange);

    const fr = rooms.filter(room => {
      // 3a) Type filter
      if (types.length && !types.includes(room.type)) return false;

      // 3b) Date filter
      if (dateRange.length) {
        // room.availableDates should be an array of YYYY-MM-DD strings
        if (!room.availableDates) return false;
        // every date in our range must be in room.availableDates
        for (let d of dateRange) {
          if (!room.availableDates.includes(d)) return false;
        }
      }
      return true;
    });

    console.log('Rooms BEFORE filter:', rooms.length, 'AFTER filter:', fr.length);
    setFilteredRooms(fr);

    // Un-check any room no longer in filtered list
    setSelectedRoomIds(prev => {
      const next = new Set();
      fr.forEach(r => { if (prev.has(r._id)) next.add(r._id) });
      return next;
    });
  }, [rooms, typeFilter, filterDates]);

  // Handlers
  const toggleType = type =>
    setTypeFilter(f => ({ ...f, [type]: !f[type] }));

  const handleDateChange = e =>
    setFilterDates(d => ({ ...d, [e.target.name]: e.target.value }));

  const toggleRoomSelection = id =>
    setSelectedRoomIds(prev => {
      const next = new Set(prev);
      next.has(id) ? next.delete(id) : next.add(id);
      return next;
    });

  const handleFormChange = e =>
    setFormData(fd => ({ ...fd, [e.target.name]: e.target.value }));

  const handleSubmit = async e => {
    e.preventDefault();
    if (!formData.agentEmail) return setMessage('Agent email is required.');
    if (!selectedHotel || !filterDates.checkIn || !filterDates.checkOut)
      return setMessage('Please select hotel and both dates.');
    if (selectedRoomIds.size === 0)
      return setMessage('Please select at least one room.');

    const roomsPayload = Array.from(selectedRoomIds).map(roomId => ({
      roomId,
      guestName: '',
      guestEmail: '',
      checkIn: filterDates.checkIn,
      checkOut: filterDates.checkOut
    }));

    try {
      const payload = {
        agentEmail: formData.agentEmail,
        discountRate: parseFloat(formData.discountRate) || 0,
        rooms: roomsPayload
      };
      const res = await API.post('/group-bookings', payload);
      setMessage('Group booking created!');
      navigate(`/group-booking/${res.data._id}`);
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.error || 'Error creating group booking');
    }
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Group Booking (Travel Agent)</h2>
      {message && <p style={{ color: 'red' }}>{message}</p>}

      <form onSubmit={handleSubmit}>
        {/* Agent Email */}
        <div>
          <label>Agent Email:</label><br/>
          <input
            type="email"
            name="agentEmail"
            value={formData.agentEmail}
            onChange={handleFormChange}
            required
          />
        </div>

        {/* Hotel Dropdown */}
        <div style={{ marginTop: '1rem' }}>
          <label>Select Hotel:</label><br/>
          <select
            value={selectedHotel}
            onChange={e => setSelectedHotel(e.target.value)}
            required
          >
            <option value="">-- Select Hotel --</option>
            {hotels.map(h => (
              <option key={h._id} value={h._id}>
                {h.name} â€” {h.location}
              </option>
            ))}
          </select>
        </div>

        {selectedHotel && (
          <>
            {/* Date Range */}
            <div style={{ marginTop: '1rem' }}>
              <label>Check-In:</label><br/>
              <input
                type="date"
                name="checkIn"
                value={filterDates.checkIn}
                onChange={handleDateChange}
                required
              />
            </div>
            <div>
              <label>Check-Out:</label><br/>
              <input
                type="date"
                name="checkOut"
                value={filterDates.checkOut}
                onChange={handleDateChange}
                required
              />
            </div>

            {/* Type Filters */}
            <div style={{ marginTop: '1rem' }}>
              <strong>Filter by Room Type:</strong><br/>
              {['Single','Double','Suite'].map(t => (
                <label key={t} style={{ marginRight: 10 }}>
                  <input
                    type="checkbox"
                    checked={typeFilter[t]}
                    onChange={() => toggleType(t)}
                  /> {t}
                </label>
              ))}
            </div>

            {/* Filtered Rooms Table */}
            <h3 style={{ marginTop: '1.5rem' }}>
              Available Rooms ({filteredRooms.length})
            </h3>
            <table
              border="1"
              cellPadding="5"
              style={{ width: '100%', borderCollapse: 'collapse' }}
            >
              <thead>
                <tr>
                  <th>Select</th>
                  <th>Room No</th>
                  <th>Type</th>
                  <th>Capacity</th>
                  <th>Price</th>
                  <th>Amenities</th>
                  <th>Available Dates</th>
                </tr>
              </thead>
              <tbody>
                {filteredRooms.map(r => (
                  <tr key={r._id}>
                    <td>
                      <input
                        type="checkbox"
                        checked={selectedRoomIds.has(r._id)}
                        onChange={() => toggleRoomSelection(r._id)}
                      />
                    </td>
                    <td>{r.roomno}</td>
                    <td>{r.type}</td>
                    <td>{r.capacity}</td>
                    <td>${r.price}</td>
                    <td>{(r.amenities || []).join(', ')}</td>
                    <td>
                      {(r.availableDates || [])
                        .sort()
                        .map(d => new Date(d).toLocaleDateString())
                        .join(', ')}
                    </td>
                  </tr>
                ))}
                {filteredRooms.length === 0 && (
                  <tr>
                    <td colSpan="7" style={{ textAlign: 'center' }}>
                      No rooms match your filters.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>

            {/* Discount & Submit */}
            <div style={{ marginTop: '1rem' }}>
              <label>Group Discount (%):</label><br/>
              <input
                type="number"
                name="discountRate"
                min="0"
                max="100"
                value={formData.discountRate}
                onChange={handleFormChange}
              />
            </div>

            <button type="submit" style={{ marginTop: '1rem' }}>
              Create Group Booking
            </button>
          </>
        )}
      </form>
    </div>
  );
};

export default GroupBookingForm;
