// src/components/BookingForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function getDatesBetween(start, end) {
  const dates = [];
  let curr = new Date(start);
  const last = new Date(end);
  while (curr < last) {
    dates.push(curr.toISOString().split('T')[0]);
    curr.setDate(curr.getDate() + 1);
  }
  return dates;
}

const BookingForm = () => {
  const navigate = useNavigate();

  // state
  const [hotels, setHotels] = useState([]);
  const [selectedHotel, setSelectedHotel] = useState('');
  const [rooms, setRooms] = useState([]);

  const [typeFilter, setTypeFilter] = useState({
    Single: false,
    Double: false,
    Suite: false
  });

  const [filterDates, setFilterDates] = useState({
    checkIn: '',
    checkOut: ''
  });

  const [message, setMessage] = useState('');

  // fetch hotels
  useEffect(() => {
    axios.get('http://localhost:5000/api/hotels')
      .then(r => setHotels(r.data))
      .catch(console.error);
  }, []);

  // fetch rooms for selected hotel
  useEffect(() => {
    if (!selectedHotel) return;
    axios.get('http://localhost:5000/api/rooms', { params: { hotel: selectedHotel } })
      .then(r => setRooms(r.data))
      .catch(console.error);
  }, [selectedHotel]);

  // handlers
  const toggleType = t =>
    setTypeFilter(f => ({ ...f, [t]: !f[t] }));

  const handleDateChange = e =>
    setFilterDates(d => ({ ...d, [e.target.name]: e.target.value }));

  const handleBook = roomId => {
    // navigate to booking page for this room
    navigate(`/booking/${roomId}`, {
      state: {
        checkIn: filterDates.checkIn,
        checkOut: filterDates.checkOut
      }
    });
  };

  // compute filtered rooms
  const filtered = rooms.filter(room => {
    // type filter
    const types = Object.entries(typeFilter)
      .filter(([_, on]) => on)
      .map(([t]) => t);
    if (types.length && !types.includes(room.type)) return false;

    // date filter
    const { checkIn, checkOut } = filterDates;
    if (checkIn && checkOut) {
      const needed = getDatesBetween(checkIn, checkOut);
      // room.availableDates is an array of "YYYY-MM-DD"
      if (!needed.every(d => room.availableDates?.includes(d))) {
        return false;
      }
    }
    return true;
  });

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Book a Room</h2>
      {message && <p style={{ color: 'red' }}>{message}</p>}

      <div>
        <label>Hotel:</label><br/>
        <select
          value={selectedHotel}
          onChange={e => setSelectedHotel(e.target.value)}
        >
          <option value="">-- Select Hotel --</option>
          {hotels.map(h => (
            <option key={h._id} value={h._id}>
              {h.name} — {h.location}
            </option>
          ))}
        </select>
      </div>

      {selectedHotel && (
        <>
          <h4>Filter by Room Type:</h4>
          {['Single','Double','Suite'].map(t => (
            <label key={t} style={{ marginRight: 10 }}>
              <input
                type="checkbox"
                checked={typeFilter[t]}
                onChange={() => toggleType(t)}
              /> {t}
            </label>
          ))}

          <div style={{ marginTop: '1rem' }}>
            <label>Check-In:</label><br/>
            <input
              type="date"
              name="checkIn"
              value={filterDates.checkIn}
              onChange={handleDateChange}
            />
          </div>
          <div>
            <label>Check-Out:</label><br/>
            <input
              type="date"
              name="checkOut"
              value={filterDates.checkOut}
              onChange={handleDateChange}
            />
          </div>

          <h4 style={{ marginTop: '1.5rem' }}>
            Available Rooms ({filtered.length})
          </h4>
          <table border="1" cellPadding="5" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Room No</th>
                <th>Type</th>
                <th>Price</th>
                <th>Amenities</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map(room => (
                <tr key={room._id}>
                  <td>{room.roomno}</td>
                  <td>{room.type}</td>
                  <td>${room.price}</td>
                  <td>{(room.amenities || []).join(', ')}</td>
                  <td>
                    <button onClick={() => handleBook(room._id)}>
                      Book
                    </button>
                  </td>
                </tr>
              ))}
              {!filtered.length && (
                <tr>
                  <td colSpan="5" style={{ textAlign: 'center' }}>
                    No rooms match your filters.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default BookingForm;
