import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { HotelAPI, BookingAPI } from '../api';

const GroupBookingForm = () => {
  const navigate = useNavigate();

  const [hotels, setHotels] = useState([]);
  const [selectedHotel, setSelectedHotel] = useState('');
  const [rooms, setRooms] = useState([]);
  const [selectedRoomIds, setSelectedRoomIds] = useState(new Set());

  const [formData, setFormData] = useState({
    agentEmail: '',
    checkIn: '',
    checkOut: '',
    discountRate: 0
  });
  const [message, setMessage] = useState('');

  // 1. Fetch all hotels on mount
  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/hotels');
        setHotels(res.data);
      } catch (err) {
        console.error('Error fetching hotels:', err);
      }
    })();
  }, []);

  // 2. When hotel changes, fetch its rooms
  useEffect(() => {
    if (!selectedHotel) return;
    (async () => {
      try {
        const res = await API.get('/rooms', { params: { hotel: selectedHotel } });
        setRooms(res.data);
        setSelectedRoomIds(new Set());
      } catch (err) {
        console.error('Error fetching rooms:', err);
      }
    })();
  }, [selectedHotel]);

  const handleHotelChange = e => setSelectedHotel(e.target.value);

  const toggleRoom = roomId => {
    setSelectedRoomIds(prev => {
      const next = new Set(prev);
      next.has(roomId) ? next.delete(roomId) : next.add(roomId);
      return next;
    });
  };

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(f => ({ ...f, [name]: value }));
  };

  // 5. Submit group booking
  const handleSubmit = async e => {
    e.preventDefault();
    if (!formData.agentEmail) {
      setMessage('Please enter your email.');
      return;
    }
    if (!selectedHotel || selectedRoomIds.size === 0) {
      setMessage('Select a hotel and at least one room.');
      return;
    }
    if (!formData.checkIn || !formData.checkOut) {
      setMessage('Please choose check-in and check-out dates.');
      return;
    }

    const roomsArray = Array.from(selectedRoomIds).map(roomId => ({
      roomId,
      checkIn: formData.checkIn,
      checkOut: formData.checkOut
    }));

    try {
      const payload = {
        agentEmail: formData.agentEmail,
        discountRate: parseFloat(formData.discountRate),
        rooms: roomsArray
      };
      const res = await API.post('/group-bookings', payload);
      setMessage('Group booking created!');
      navigate(`/group-booking/${res.data._id}`);
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.error || 'Error creating group booking');
    }
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Travel Agent: Create Group Booking</h2>
      {message && <p style={{ color: 'red' }}>{message}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Agent Email:</label><br/>
          <input
            type="email"
            name="agentEmail"
            value={formData.agentEmail}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Select Hotel:</label><br/>
          <select value={selectedHotel} onChange={handleHotelChange} required>
            <option value="">--Select Hotel--</option>
            {hotels.map(h => (
              <option key={h._id} value={h._id}>
                {h.name} — {h.location}
              </option>
            ))}
          </select>
        </div>

        {rooms.length > 0 && (
          <div>
            <h3>Pick Rooms:</h3>
            {rooms.map(r => (
              <div key={r._id}>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedRoomIds.has(r._id)}
                    onChange={() => toggleRoom(r._id)}
                  />{' '}
                  {r.roomno} — {r.type} — ${r.price}
                </label>
              </div>
            ))}
          </div>
        )}

        <div>
          <label>Check-In:</label><br/>
          <input
            type="date"
            name="checkIn"
            value={formData.checkIn}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Check-Out:</label><br/>
          <input
            type="date"
            name="checkOut"
            value={formData.checkOut}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Group Discount (%):</label><br/>
          <input
            type="number"
            name="discountRate"
            min="0"
            max="100"
            value={formData.discountRate}
            onChange={handleChange}
          />
        </div>

        <button type="submit" style={{ marginTop: '1rem' }}>
          Create Group Booking
        </button>
      </form>
    </div>
  );
};

export default GroupBookingForm;
