import React, { useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';

const BookingForm = () => {
  const [formData, setFormData] = useState({
    roomId: '',
    guestName: '',
    guestEmail: '',
    checkIn: '',
    checkOut: '',
    additionalServices: {
      breakfast: false,
      airportTransport: false,
      specialAccommodations: ''
    }
  });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (name in formData.additionalServices) {
      setFormData((prev) => ({
        ...prev,
        additionalServices: {
          ...prev.additionalServices,
          [name]: type === 'checkbox' ? checked : value
        }
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post('/bookings', formData);
      setMessage('Booking created successfully!');
      // Optionally navigate to booking details page
      navigate(`/booking/${res.data.booking._id}`);
    } catch (err) {
      setMessage(err.response?.data?.msg || 'Error creating booking');
    }
  };

  return (
    <div>
      <h2>Create a New Booking</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Room ID:</label>
          <input type="text" name="roomId" value={formData.roomId} onChange={handleChange} required />
        </div>
        <div>
          <label>Guest Name:</label>
          <input type="text" name="guestName" value={formData.guestName} onChange={handleChange} required />
        </div>
        <div>
          <label>Guest Email:</label>
          <input type="email" name="guestEmail" value={formData.guestEmail} onChange={handleChange} required />
        </div>
        <div>
          <label>Check In (YYYY-MM-DD):</label>
          <input type="date" name="checkIn" value={formData.checkIn} onChange={handleChange} required />
        </div>
        <div>
          <label>Check Out (YYYY-MM-DD):</label>
          <input type="date" name="checkOut" value={formData.checkOut} onChange={handleChange} required />
        </div>
        <div>
          <label>Breakfast:</label>
          <input type="checkbox" name="breakfast" checked={formData.additionalServices.breakfast} onChange={handleChange} />
        </div>
        <div>
          <label>Airport Transport:</label>
          <input type="checkbox" name="airportTransport" checked={formData.additionalServices.airportTransport} onChange={handleChange} />
        </div>
        <div>
          <label>Special Accommodations:</label>
          <input type="text" name="specialAccommodations" value={formData.additionalServices.specialAccommodations} onChange={handleChange} />
        </div>
        <button type="submit">Create Booking</button>
      </form>
    </div>
  );
};

export default BookingForm;
